mapper.update:
UPDATE product
prod_name = #{prodName} , prod_detail = #{prodDetail}, manufacture_day = #{manuDate} , price = #{price}, reg_date = #{regDate},
prod_trancode = #{proTranCode}
WHERE prod_no = #{prodNo}

mapper.select:
  	SELECT COUNT(*)
  	FROM(	SELECT prod_no , prod_name , prod_trancode
					FROM product
					
						
							
					 			prod_no = #{searchKeyword}
							
							
					 			prod_name LIKE '%' || #{searchKeyword} || '%'
					 			
							
						
					 ) countTable
